children.script:
var myButtons = document.getElementsByClassName("myButton");

for (let i=0; i<myButtons.length; i++) {
	myButtons[i].style["border"] = "2px solid black";
	myButtons[i].style["background-color"] = "#ffffff50";
	myButtons[i].style["border-radius"] = "15px";
	myButtons[i].style["font-size"] = "20px";
	myButtons[i].style["filter"] = "drop-shadow(0px 5px 3px #00000030)";
};
